import './Modal.css'
import reactDom from 'react-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimesCircle } from '@fortawesome/free-solid-svg-icons';
import {useSpring, animated, useTransition} from 'react-spring'
import { config } from '@fortawesome/fontawesome-svg-core';
const Modal = props => {
    const animation = useTransition(props.isOpen,{
        from:{opacity: 0},
        enter:{opacity: 1},
        leave:{opacity: 0},
        reverse: !props.isOpen,
        config: config.molasses

    })


    if(!props.isOpen) return null;

    return reactDom.createPortal(
    <> 
    <div onClick={props.onClose} className="overlay"/>

        <div className="modalCard">
        {animation((styles, item) => item && <animated.div style={styles}>

    <FontAwesomeIcon className="close" icon={faTimesCircle} onClick={props.onClose}/>
    <div className="innerContent">
    {props.children}
    </div>
    </animated.div>)}


        </div>

        </>




    ,document.getElementById('modal'))

}

export default Modal
