import React, { useState, useEffect } from 'react';
const LockScreen = () => {
    const [emailVal,setEmailVal] = useState('')
    const [passwordVal,setPasswordVal] = useState('')
    const [emailError,setEmailError] = useState()
    const [passwordError, setPasswordError] = useState()
    const emailChange = e => setEmailVal(e.target.value.trim())
    const passwordChange = e => setPasswordVal(e.target.value)

useEffect(() => {
    const passwordTimer = setTimeout(() => {
        if(passwordVal.length < 6) setPasswordError('Prekratko')
        if(passwordVal.length >= 6 && passwordError.length > 1) setPasswordError()
    },500)
    return () => clearTimeout(passwordTimer)
},[passwordVal])




    return <div className="lock">
        <div className="login">
            <form>
                <p>Email</p>
                <p className="error">{emailError}</p>
                <input
                    type="email"
                    value={emailVal}
                    onChange={emailChange}/>
                <p>Lozinka</p>
                <p className="error">{passwordError}</p>
                <input  type="password"
                        value={passwordVal}
                        onChange={passwordChange}/>
                <div><button type="submit">Prijavi se</button></div>
            </form>
        </div>
    </div>
}

export default LockScreen
