import React, { useState, useEffect } from 'react';

const Ucenici = props => {
    const [ucenici, setUcenici] = useState([
        {
            id: 2,
            name: "Mate Peric",
            school: "srednja",
            phone: "0955445968",
            razred: 3,
            placeno: false

        },
        {
            id: 3,
            name: "Ana Matic",
            school: "osnovna",
            phone: "098786",
            razred: 4,
            placeno: true
        }
    ])
    const [currUcenici, setCurrUcenici] = useState(ucenici)
    useEffect(() => {

    }, [currUcenici])

    const onChangeFilter = e => {
        setCurrUcenici(
            ucenici.filter(
                ucenik => ucenik.name.toLowerCase()
                .includes(e.target.value.toLowerCase()
                )));

    }

    return <div>
        <input type="text" onChange={onChangeFilter}/>
    </div>
}

export default Ucenici
