import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import MainWrapper from '../../UI/MainWrapper';
import './Registracija.css'

const Registracija = () => {
let history = useHistory();
const [username, setUsername] = useState('')
const [userFound,setUserFound] = useState(false)
useEffect(() => {
    const userTimer = setTimeout(() => {
        if(username.length > 3) setUserFound(true)
    }, 1000)
    return () => clearTimeout(userTimer)
},[username])


return <div className="registracija">
    <h2>Registracija</h2>
    <form>
        <label>Korisničko ime</label>
        <input type="text" placeholder='LDAP korisničko ime' />
        <label>Datum rođenja</label>
        <input type="date"/>
        <label>Spol</label>
        <div className="radios">
        <label htmlFor="musko">Muško</label>
        <input type="checkbox" name='Musko' id='musko'/>

        <label htmlFor="zensko">Žensko</label>
        <input type="radio" name='zensko' id='zensko'/>

        <label htmlFor="drugo">Drugo</label>
        <input type="radio" name='Drugo' id='drugo'/>

        </div>

    </form>
</div>
}

export default Registracija
