import EventForm from "../EventForm/EventForm";
import React, { useState } from 'react';
import Modal from "../Modal/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserPlus, faCalendarPlus, faTrash } from "@fortawesome/free-solid-svg-icons";
import UserForm from "../EventForm/UserForm";
import './CalendarModule.css'
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css"
import EventInfo from "../EventForm/EventInfo";

const CalendarModule = () => {
    const [modalText, setModalText] = useState();
    const [events,setEvents] = useState([
        {
            id: 1,
            title: "[ENG] Mate Peric",
            start: new Date(2021,10,12,17,30),
            end: new Date(2021,10,12,19,0),
            class: 'eng',
            student: "Mate Peric"
        }
    ])
    const [users,setUsers] = useState([
        {
            id: 2,
            name: "Mate Peric",
            school: "srednja",
            phone: "0955445968"

        },
        {
            id: 3,
            name: "Ana Matic",
            school: "osnovna",
            phone: "098786"
        }
    ])
    const [isOpen, setIsOpen] = useState(false)
    const [isUserOpen, setIsUserOpen] = useState(false)
    const onEventForm = event => setEvents([event, ...events])
    const onUserForm = user => setUsers([user, ...users])
    const [eventOpen, setEventOpen] = useState(false)
    const [selectedUser, setSelectedUser] = useState()
    const [selectedEvent, setSelectedEvent] = useState()
    // Calendar stuff
    const locales = {
        "hr": require("date-fns/locale/hr")
      }
      const localizer = dateFnsLocalizer({
        format,
        parse,
        startOfWeek: () => startOfWeek(new Date(), {weekStartsOn: 1}),
        getDay,
        locales
      });
// end of calendar stuff
 const onSelected = e => {
     const currUser = users[users.findIndex(user => user.name === e.student)]
     setSelectedUser(currUser)
     setSelectedEvent(e)




 }

const eventDelete = id => {
    const newEvents = events.filter(event => event.id !== id)
    setEvents(newEvents)
    setEventOpen(false)
}



    return <div className="calendarModule">
        <Calendar
        onSelectEvent={onSelected} 
        localizer={localizer}
        startAccessor="start"
        endAccessor="end"
        events={events}
        style={{height: 500 
                }}  />
        <div className="buttonWrapper">
        <button
            onClick={() => setIsOpen(true)}
            className="modalTrigger">
            <FontAwesomeIcon 
                icon={faCalendarPlus}
                size={"lg"}/>
        </button>
        <button className="modalTrigger"
                onClick={() => setIsUserOpen(true)}>
                <FontAwesomeIcon
                    icon={faUserPlus}
                    size={"lg"}/>
                    </button>
        </div>
                    <div>
        <Modal onClose={() => setIsOpen(false)} isOpen={isOpen}>
            <EventForm 
                onEventForm={onEventForm} 
                users={users} 
                onClose={() => setIsOpen(false)}/>
        </Modal>
        </div>

        <Modal onClose={() => setIsUserOpen(false)} isOpen={isUserOpen}>
            <UserForm 
                onClose={() => setIsUserOpen(false)}
                onUserForm={onUserForm}></UserForm>
        </Modal>
        <Modal isOpen={eventOpen} onClose={() => setEventOpen(false)}>
        <EventInfo
        selectedEvent={selectedEvent}
        selectedUser={selectedUser}/>
        </Modal>
        </div>


}
export default CalendarModule
