import Modal from "../Modal/Modal";
import moment from "moment";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";

const EventInfo = props => {
    const [isValid, setIsValid] = useState(false)

    const [modalText,setModalText] = useState('')
useEffect(() => {

    const user = props.selectedUser
    const event = props.selectedEvent
    console.log(user);
    console.log(event);
    const onClickPlaceno = () => props.onPlaceno(user)
    const onClickDelete = () => props.onDelete(user.id)
    if(user === undefined || event === undefined) return null;
    setModalText(
        <>
        <h2>{event.title}</h2>
        <h3>{moment(event.start).format('hh:mm')} - {moment(event.end).format('hh:mm')}</h3>
        <p>Å kola: {user.school}</p>
        <p>razred: {user.razred}</p>
        {user.placeno ? <p>Placeno</p> : <p>Nije placeno</p>}
        <div>
            <button onClick={props.onClose} className="primary">OK</button>
            <button 
                className="secondary"
                onClick={onClickPlaceno}>
                {!user.placeno ? 'Platio' : 'Nije platio'}
            </button>
            <button 
                className="trash"
                onClick={onClickDelete}>
                    <FontAwesomeIcon
                    icon={faTrash}
                    size={"lg"}/>
            </button>
        </div>
        </>
    )


},
[props.selectedUser,props.selectedEvent])
useEffect(() => {
    console.log(modalText);
    setIsValid(true)
}, 
[modalText])
if(!isValid) return null;
return modalText

}


export default EventInfo
