import React, { useState, useEffect } from 'react';
import MainWrapper from '../../UI/MainWrapper';
import './biljeske.css'
const Biljeske = () => {
const [biljeske, setBiljeske] = useState([
    {
        tekst: 'test',
        boja: 'purple',
        id: 1
    }
])
const [tekst,setTekst] = useState()
const [boja,setBoja] = useState()

const onDelete = id => setBiljeske(biljeske.filter(b => b.id === id))
const onClick = e => setBoja(e.target.style.backgroundColor);
const onSubmit = e => {
    e.preventDefault()
    const newNote = {
        tekst,
        boja,
        id: biljeske.length +1
    }
    setBiljeske([newNote, ...biljeske])
}



return <MainWrapper>
    <div className="biljeske">
        <form>
            <input type="text"/>
            <button type="submit" className="primary">Dodaj</button>
            <div className="colors">
                <button className="clrBtn" onClick={onClick} style={{backgroundColor: 'lightgreen'}}></button>
                <button className="clrBtn" onClick={onClick} style={{backgroundColor: 'lightblue'}}></button>
                <button className="clrBtn" onClick={onClick} style={{backgroundColor: 'violet'}}></button>
            </div>
        </form>
    </div>
</MainWrapper>


}

export default Biljeske
