import moment from "moment";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashAlt } from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";

const EventInfo = props => {

    const [newObject,setNewObject] = useState({
        event_id:null,
        user_id:null,
        start:null,
        end:null,
        user_name:null,
        class:null,
        title:null,
        school:null
    })




useEffect(() => {

    const user = props.selectedUser
    const event = props.selectedEvent
    console.log(user);
    console.log(event);
    setNewObject({
        event_id: event.id,
        user_id: user.id,
        start: moment(event.start).format('hh:mm'),
        end: moment(moment(event.end).format('hh:mm')),
        user_name: user.name,
        class: event.class,
        title: event.title,
        school: user.school
    })
    console.log(props.selectedEvent);
},
[props.selectedUser,props.selectedEvent])

return <>
    <h3>cute</h3>
</>
}


export default EventInfo
