import React, { useState } from 'react';
import './Form.css'
const EventForm = props => {
    const [ime, setIme] = useState('')
    const [startTime, setStartTime] = useState()
    const [predmet, setPredmet] = useState('')
    const [endTime, setEndTime] = useState('')
    const [naslov,setNaslov] = useState('')
    const [note,setNote] = useState()

    const onIme = e => setIme(e.target.value)
    const onStartTime = e => setStartTime(new Date(e.target.value))
    const onPredmet = e => setPredmet(e.target.value)

    const submitHandler = e => {
        e.preventDefault()
        if(!predmet) {alert('Odaberi predmet!'); return}
        const newEvent = {
            start: startTime,
            student: ime,
            class: predmet,
            title: `[${predmet.toUpperCase()}] ${ime}`,
            end: endTime,
            placeno: false
        }
        props.onEventForm(newEvent)
        props.onClose()
    }


    return <form className="modalForm" onSubmit={submitHandler} >
            <label>Naslov</label>
            <input type="text" required />
            <div style={{width: '50%'}}>
                <label>Start</label>
                <input type="datetime-local"  />
            </div>
            <span style={{width: '50%'}}>
                <label>Kraj</label>
                <input type="datetime-local"  />
            </span>

        <div>
        <button className="primary" type="submit">Dodaj</button>
        <button className="secondary" onClick={props.onClose}>Odustani</button>
        </div>
    </form>
}

export default EventForm
