import React, { useState, useEffect } from 'react';
const LockScreen = () => {
    const [emailVal,setEmailVal] = useState('')
    const [passwordVal,setPasswordVal] = useState('')
    const [emailError,setEmailError] = useState()
    const emailChange = e => setEmailVal(e.target.value.trim())
    const passwordChange = e => setPasswordVal(e.target.value)
    useEffect(() => {
        const emailTimer = setTimeout(() => {
            if(!emailVal.includes('foi.hr')) setEmailError('Adresa mora sadržavati @foi.hr')
            else setEmailError()},500)
            return () => clearTimeout(emailTimer)
    }, [emailVal])




    return <div className="lock">
        <div className="login">
            <form>
                <p>Email</p>
                <p className="error">{emailError}</p>
                <input
                    type="email"
                    value={emailVal}
                    onChange={emailChange}/>
                <p>Lozinka</p>
                <input  type="password"
                        value={passwordVal}
                        onChange={passwordChange}/>
                <div><button type="submit">Prijavi se</button></div>
            </form>
        </div>
    </div>
}

export default LockScreen
