import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import MainWrapper from '../../UI/MainWrapper';
import './Registracija.css'

const Registracija = () => {
let history = useHistory();
const [username, setUsername] = useState('')
const [userFound,setUserFound] = useState(false)
useEffect(() => {
    const userTimer = setTimeout(() => {
        if(username.length > 3) setUserFound(true)
    }, 1000)
    return () => clearTimeout(userTimer)
},[username])


return <div className="background">
    <div className="registracija">
    <h2>Registracija</h2>
    <div className="step">
        <h4>Korak 1 od 2</h4>
    </div>
    <form>
        <p style={{color: 'green'}}>{userFound ? 'Korisnik pronađen!' : ''}</p>
        <label>Korisničko ime</label>
        <input type="text" placeholder='LDAP korisničko ime' />
        <label>Datum rođenja</label>
        <input type="date"/>
        <label>Spol</label>
        <select>
            <option value={undefined}>Odaberite</option>
            <option value="muško">Muško</option>
            <option value="žensko">Žensko</option>
            <option value="drugo">Drugo</option>
        </select>
        <label>Lozinka</label>
        <input type="password"  />
        <label>Ponovite lozinku</label>
        <input type="password" />
        <button className="secondary">Odustani</button>
        <button className="primary">Dalje</button>

    </form>
</div>
</div>
}

export default Registracija
